// Application configuration
const config = {
  // API Configuration
  API_BASE_URL: process.env.REACT_APP_API_URL || 'http://localhost:8000',

  // Environment
  ENV: process.env.REACT_APP_ENV || 'development',

  // Feature flags
  ENABLE_ANALYTICS: process.env.REACT_APP_ENABLE_ANALYTICS === 'true',
  ENABLE_DEBUG_LOGGING: process.env.REACT_APP_ENABLE_DEBUG_LOGGING !== 'false',

  // API timeouts
  API_TIMEOUT: 10000,

  // Table configuration
  TABLE_PAGE_SIZE: 50,
  TABLE_MAX_PAGE_SIZE: 1000,

  // Currency formatting
  CURRENCY_LOCALE: 'en-GB',
  CURRENCY_OPTIONS: {
    style: 'currency',
    currency: 'GBP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0,
  },

  // Number formatting
  NUMBER_LOCALE: 'en-GB',
  PERCENTAGE_OPTIONS: {
    minimumFractionDigits: 1,
    maximumFractionDigits: 2,
  },

  // Date formatting
  DATE_LOCALE: 'en-GB',
  DATE_OPTIONS: {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
  },
};

export default config;
