import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [portfolioData, setPortfolioData] = useState(null);
  const [summary, setSummary] = useState(null);
  const [allocations, setAllocations] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError(null);

      // Fetch all data in parallel
      const [portfolioRes, summaryRes, allocationsRes] = await Promise.all([
        axios.get(`${API_BASE_URL}/portfolio/current`),
        axios.get(`${API_BASE_URL}/portfolio/summary`),
        axios.get(`${API_BASE_URL}/portfolio/allocations`)
      ]);

      setPortfolioData(portfolioRes.data);
      setSummary(summaryRes.data);
      setAllocations(allocationsRes.data);
    } catch (err) {
      console.error('Error fetching data:', err);
      setError('Failed to load portfolio data. Please check if the backend is running.');
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-GB', {
      style: 'currency',
      currency: 'GBP'
    }).format(amount);
  };

  const formatPercentage = (value) => {
    return `${value >= 0 ? '+' : ''}${value.toFixed(2)}%`;
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading">Loading portfolio data...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container">
        <div className="error">{error}</div>
        <button onClick={fetchData}>Retry</button>
      </div>
    );
  }

  return (
    <div className="container">
      <h1>Trading212 Portfolio Dashboard</h1>

      {/* Portfolio Summary */}
      {summary && (
        <div className="card">
          <h2>Portfolio Summary</h2>
          <div className="stats-grid">
            <div className="stat-card">
              <div className="stat-value">{formatCurrency(summary.total_value)}</div>
              <div className="stat-label">Total Value</div>
            </div>
            <div className="stat-card">
              <div className={`stat-value ${summary.total_profit >= 0 ? 'positive' : 'negative'}`}>
                {formatCurrency(summary.total_profit)}
              </div>
              <div className="stat-label">Total Profit/Loss</div>
            </div>
            <div className="stat-card">
              <div className={`stat-value ${summary.total_return_pct >= 0 ? 'positive' : 'negative'}`}>
                {formatPercentage(summary.total_return_pct)}
              </div>
              <div className="stat-label">Total Return</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{summary.total_holdings}</div>
              <div className="stat-label">Total Holdings</div>
            </div>
            <div className="stat-card">
              <div className="stat-value positive">{summary.profitable_holdings}</div>
              <div className="stat-label">Profitable</div>
            </div>
            <div className="stat-card">
              <div className="stat-value negative">{summary.losing_holdings}</div>
              <div className="stat-label">Losing</div>
            </div>
            <div className="stat-card">
              <div className="stat-value">{summary.win_rate.toFixed(1)}%</div>
              <div className="stat-label">Win Rate</div>
            </div>
          </div>
          {summary.last_updated && (
            <p><small>Last updated: {new Date(summary.last_updated).toLocaleString()}</small></p>
          )}
        </div>
      )}

      {/* Allocations */}
      {allocations && (
        <div className="card">
          <h2>Portfolio Allocations</h2>
          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px' }}>
            <div>
              <h3>Sector Allocation</h3>
              <table>
                <thead>
                  <tr>
                    <th>Sector</th>
                    <th>Percentage</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(allocations.sector_allocation)
                    .sort(([,a], [,b]) => b - a)
                    .map(([sector, percentage]) => (
                      <tr key={sector}>
                        <td>{sector}</td>
                        <td>{percentage.toFixed(2)}%</td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
            <div>
              <h3>Country Allocation</h3>
              <table>
                <thead>
                  <tr>
                    <th>Country</th>
                    <th>Percentage</th>
                  </tr>
                </thead>
                <tbody>
                  {Object.entries(allocations.country_allocation)
                    .sort(([,a], [,b]) => b - a)
                    .map(([country, percentage]) => (
                      <tr key={country}>
                        <td>{country}</td>
                        <td>{percentage.toFixed(2)}%</td>
                      </tr>
                    ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}

      {/* Holdings Table */}
      {portfolioData && (
        <div className="card">
          <h2>Current Holdings</h2>
          <table>
            <thead>
              <tr>
                <th>Symbol</th>
                <th>Name</th>
                <th>Quantity</th>
                <th>Avg Price</th>
                <th>Current Price</th>
                <th>Market Value</th>
                <th>P&L</th>
                <th>Return %</th>
                <th>Sector</th>
                <th>Country</th>
              </tr>
            </thead>
            <tbody>
              {portfolioData.holdings
                .sort((a, b) => b.market_value - a.market_value)
                .map((holding) => (
                  <tr key={holding.t212_code}>
                    <td>{holding.yahoo_symbol || holding.t212_code}</td>
                    <td>{holding.name}</td>
                    <td>{holding.quantity.toFixed(2)}</td>
                    <td>{formatCurrency(holding.avg_price)}</td>
                    <td>{formatCurrency(holding.current_price)}</td>
                    <td>{formatCurrency(holding.market_value)}</td>
                    <td className={holding.ppl >= 0 ? 'positive' : 'negative'}>
                      {formatCurrency(holding.ppl)}
                    </td>
                    <td className={holding.return_pct >= 0 ? 'positive' : 'negative'}>
                      {formatPercentage(holding.return_pct)}
                    </td>
                    <td>{holding.sector || 'N/A'}</td>
                    <td>{holding.country || 'N/A'}</td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
